19 {
20     // GPS data collection
21     while (SERIALGPS.available() > 0){
22         gps.encode(SERIALGPS.read());
23     }
24 
25     // Updates STATUS WORD
26     if (gps.date.isValid())                             //Date
27     {
28         resetSTGPSDate;
29         if (gps.date.isUpdated())
30         {
31             LCD_UpdateDate(gps.date.year(), gps.date.month(), gps.date.day());
32         }
33     }
34     else
35     {
36         setSTGPSDate;
37         LCD_UpdateDate(0,0,0);
38     }
39 
40     if (gps.time.isValid())                             //Time
41     {
42         resetSTGPSTime;
43         if (gps.time.isUpdated())
44         {
45             LCD_UpdateTime(gps.time.hour(), gps.time.minute(), gps.time.second());
46         }
47     }
48     else
49     {
50         setSTGPSTime;
51         LCD_UpdateTime(0,0,0);
52     }
53 
54 
55     if (gps.satellites.isValid())
56     {
57         if (gps.satellites.isUpdated())
58         {
59             LCD_UpdateSat(gps.satellites.value());
60         }
61     }else
62     {
63         LCD_UpdateSat(0);
64     }   
65     
66 }

