297 {
298     char data[18] = "";
299     
300     // Once any field of the date is less than 10, the information received would have only one character.
301     // In order to standardize the data, a 0 would be added before the number.
302     char zyear[2];
303     char zmonth[2];
304     char zday[2];
305     
306     
307     (y < 10) ? (strcpy(zyear, "0"))  : (strcpy(zyear, ""));
308     (m < 10) ? (strcpy(zmonth, "0")) : (strcpy(zmonth, ""));
309     (d < 10) ? (strcpy(zday, "0"))   : (strcpy(zday, ""));
310     
311     sprintf(data,"%s%d/%s%d/%s%d", zy, y, zm, m, zd, d);
312     LCD_Text(5, 220, data);
313     
314 }

