332         
333     (h < 10) ? (strcpy(zhour, "0")) : (strcpy(zhour, ""));
334     (m < 10) ? (strcpy(zmin, "0")) : (strcpy(zmin, ""));
335     (s < 10) ? (strcpy(zsec, "0")) : (strcpy(zsec, ""));
