32 {
33     unsigned long r;
34     if (c - l >=intervalSTATUS) {
35         if (WiFi.status() != WL_CONNECTED)                  // WiFi with 2 minutes refresh interval
36         {
37             LCD_UpdateWIFIStatus(0);
38             SERIALDEBUG.println("WiFi Lost connection");
39             WiFi.begin(ssid, password);
40             while (WiFi.status() != WL_CONNECTED)           // Waits for a new connection
41             {
42                 delay(500);
43                 SERIALDEBUG.print(".");
44             }
45             LCD_UpdateWIFIStatus(1);
46             SERIALDEBUG.println("");
47             SERIALDEBUG.println("WiFi reconnected");
48             r = c;
49         }else 
50         {
51             LCD_UpdateWIFIStatus(1);
52             r = l;
53         }
54         
55     }else r = l;
56     return r;
57 }
